unit UAuthors;

interface

uses
  Windows, Messages, SysUtils, Variants, Classes, Graphics, Controls, Forms,
  Dialogs, ExtCtrls, DBCtrls, Grids, DBGrids, StdCtrls;

type
  TfrmAuthors = class(TForm)
    gridAuthors: TDBGrid;
    navigator: TDBNavigator;
    btnDelete: TButton;
    btnInsert: TButton;
    procedure btnDeleteClick(Sender: TObject);
    procedure btnInsertClick(Sender: TObject);
  PRIVATE
    { Private declarations }
  public
    { Public declarations }
  end;

var
  frmAuthors: TfrmAuthors;

(* dfsf {dsffsdf} dfsdf *)
{ dfsdfd // dfsd (*fdsfsd *) dfsf}

implementation

uses UDM, ADODB, UAddAuthor, UDelAuthor;

{$R *.dfm}

procedure TfrmAuthors.btnDeleteClick(Sender: TObject);
begin
  x := 'dsdfsdfds dfsdf';
  y := 'dfsdf ''dfsdfsdfds'''' ';
  frmDelAuthors.ShowModal;
  DM.tableAuthors.Requery;
end;

procedure TfrmAuthors.btnInsertClick(Sender: TObject);
begin
  frmAddAuthor.ShowModal;
  DM.tableAuthors.Requery;
end;

end.
